import {
  Button,
  ButtonGroup,
  Grid,
  TextField,
  Typography,
} from "@mui/material";
import React from "react";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import shiftsUtils from "../Utils/shiftsUtils";
import ShiftAllocate from "../Components/ShiftAllocate";
import ShiftComp from "../Components/Shift";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import { Store } from "@mui/icons-material";
import Paper from "@mui/material/Paper";
import { styled } from "@mui/material/styles";

const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

const ShiftsPage = () => {
  let token = sessionStorage["token"];
  const storeData = useSelector((state) => state);
  const [value, setValue] = React.useState(new Date("2022-08-18T21:11:54"));

  const [addNewShift, setAddNewShift] = useState(false);
  const [startDate, setStartDate] = useState(new Date());
  const [startHour_new, setStartHour_new] = useState("");
  const [endingHour_new, setEndingHour_new] = useState("");
  const [added, setAdded] = useState(false);

  const [updateShift, setUpdateShift] = useState(false);

  const [allocateShift, setAllocateShift] = useState(false);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  // useEffect(
  //   async () => {
  //     let resp = await axios.get("http://127.0.0.1:5000/getAllFactory");
  //     let data = resp.data;
  //     setAllDbData(data);
  //   },
  //   [updateShift, addNewShift],
  //   [allocateShift]
  // );
  const convertDate = (str) => {
    str = str.toString();
    let parts = str.split(" ");
    let months = {
      Jan: "01",
      Feb: "02",
      Mar: "03",
      Apr: "04",
      May: "05",
      Jun: "06",
      Jul: "07",
      Aug: "08",
      Sep: "09",
      Oct: "10",
      Nov: "11",
      Dec: "12",
    };
    return parts[2] + "/" + months[parts[1]] + "/" + parts[3];
  };

  const add_shift = async () => {
    let obj = {};
    let date = convertDate(startDate);
    obj.Date = date;
    obj.StartingHour = startHour_new;
    obj.EndingHour = endingHour_new;

    await shiftsUtils.add_shift(token, obj);
    setAdded(!added);
  };

  return (
    <div>
      <Typography variant="h4" pb={2} color="primary.second">
        <b> Shifts Page</b>
      </Typography>
      <div>
        <Typography variant="h6" pb={1} color="primary.second">
          <b> Add new shift</b>
        </Typography>{" "}
        <div>
          <Typography variant="body2" color="primary.second">
            Choose Date:
          </Typography>{" "}
          <DatePicker
            popperClassName="dp"
            showDisabledMonthNavigation
            popperPlacement="bottom"
            dateFormat="dd/MM/yyyy"
            selected={startDate}
            onChange={(date) => setStartDate(date)}
          />
        </div>
        <TextField
          id="outlined-basic"
          label="Starting Hour"
          variant="outlined"
          fullWidth
          placeholder="08:00"
          required
          onChange={(e) => setStartHour_new(e.target.value)}
          sx={{ my: 1 }}
          // error={LastNameError}
        />
        <TextField
          id="outlined-basic"
          label="Ending Hour"
          variant="outlined"
          fullWidth
          placeholder="17:30"
          required
          onChange={(e) => setEndingHour_new(e.target.value)}
          sx={{ my: 1 }}
          // error={LastNameError}
        />
        <Button
          fullWidth
          sx={{ mb: 1 }}
          variant="contained"
          onClick={add_shift}
        >
          Add shift
        </Button>
        {added && <div>Added!</div>}
      </div>

      <div>
        <ButtonGroup fullWidth>
          <Button
            fullWidth
            sx={{ mr: 1 }}
            variant="contained"
            onClick={() => setUpdateShift(!updateShift)}
          >
            Update shift
          </Button>
          <Button
            fullWidth
            sx={{ ml: 1 }}
            variant="contained"
            onClick={() => setAllocateShift(!allocateShift)}
          >
            Allocate employees to shift
          </Button>
        </ButtonGroup>
      </div>
      {allocateShift && (
        <div>
          <h3>Allocate Shifts:</h3>
          <Grid container spacing={3}>
            {storeData.db.shifts.map((item, index) => {
              return (
                <Grid item xs={12} md={6} s={1} key={index}>
                  <Item>
                    <ShiftAllocate
                      shift={item}
                      employees={storeData.db.employees}
                    />
                  </Item>
                </Grid>
              );
            })}
          </Grid>
        </div>
      )}
      {updateShift && (
        <div>
          <h3>Update Shifts:</h3>

          <Grid container spacing={3}>
            {storeData.db.shifts.map((item, index) => {
              return (
                <Grid item xs={12} md={6} s={1} key={index}>
                  <Item>
                    <ShiftComp
                      shift={item}
                      employees={storeData.db.employees}
                    />
                  </Item>
                </Grid>
                // <ShiftComp
                //   key={index}
                //   shift={item}
                //   employees={storeData.db.employees}
                // />
              );
            })}
          </Grid>
        </div>
      )}
    </div>
  );
};

export default ShiftsPage;
