import React from "react";
import { useEffect, useState } from "react";
import axios from "axios";
import departmentUtils from "../Utils/departmentUtils";
import { useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import {
  InputLabel,
  MenuItem,
  Select,
  TextField,
  Typography,
} from "@mui/material";
const NewDepartmentPage = () => {
  const storeData = useSelector((state) => state);
  let token = sessionStorage["token"];
  const navigate = useNavigate();

  const [newDep, setNewDep] = useState({ Name: "", Manager: "" });
  const [depManager, setDepManager] = useState("");
  const [emps, setEmps] = useState([]);
  const [newDepError, setNewDepError] = useState(false);

  useEffect(async () => {
    let all_db_data = storeData.db;
    let emp = all_db_data.employees;
    setEmps(emp);
  }, []);

  const handleChange = (e) => {
    setNewDep({ ...newDep, Manager: e.target.value });
  };

  const save = async () => {
    await departmentUtils.add_department(token, newDep);
  };

  return (
    <div>
      <Typography variant="h4" pb={2} color="primary.second">
        <b> New Department Page</b>
      </Typography>{" "}
      <TextField
        id="outlined-basic"
        label="Department name"
        required
        fullWidth
        error={newDepError}
        onChange={(e) => setNewDep({ ...newDep, Name: e.target.value })}
      />
      <InputLabel id="demo-simple-select-label">Choose Department</InputLabel>
      <Select
        labelId="demo-simple-select-label"
        id="demo-simple-select"
        // value={{depManager}}
        value={newDep.Manager}
        label="Choose Manager"
        onChange={handleChange}
        // autoWidthful
      >
        {emps.map((item, index) => {
          return (
            <MenuItem key={index} value={item._id}>
              {item.FirstName} {item.LastName}{" "}
            </MenuItem>
          );
        })}
        <MenuItem value="Cancel">Cancel filter</MenuItem>
      </Select>
      Name:{" "}
      <input
        type="text"
        onChange={(e) => setNewDep({ ...newDep, Name: e.target.value })}
      />
      <br />
      Manager:{" "}
      <select onChange={(e) => handleChange(e)}>
        <option value="" hidden selected>
          Selected Manager
        </option>
        {emps.map((item, index) => {
          return (
            <option key={index} value={item._id}>
              {item.FirstName} {item.LastName}
            </option>
          );
        })}
      </select>
      <br />
      <br />
      <input type="button" value="save" onClick={save} />
      <input
        type="button"
        value="cancel"
        onClick={() => navigate("/mainPage/departmentPage")}
      />
    </div>
  );
};

export default NewDepartmentPage;
