import { Button, ButtonGroup, TextField, Typography } from "@mui/material";
import React from "react";
import { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import shiftsUtils from "../Utils/shiftsUtils";
import DatePicker from "react-datepicker";
import ShiftAllocate from "../Components/ShiftAllocate";
import ShiftComp from "../Components/Shift";
import DateAdapter from "@mui/lab/AdapterDateFns";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";

const ShiftsPage = () => {
  let token = sessionStorage["token"];
  const storeData = useSelector((state) => state);
  const [value, setValue] = React.useState(new Date("2022-08-18T21:11:54"));

  const [addNewShift, setAddNewShift] = useState(false);
  const [startDate, setStartDate] = useState(new Date());
  const [startHour_new, setStartHour_new] = useState("");
  const [endingHour_new, setEndingHour_new] = useState("");
  const [added, setAdded] = useState(false);

  const [updateShift, setUpdateShift] = useState(false);

  const [allocateShift, setAllocateShift] = useState(false);

  const handleChange = (newValue) => {
    setValue(newValue);
  };

  // useEffect(
  //   async () => {
  //     let resp = await axios.get("http://127.0.0.1:5000/getAllFactory");
  //     let data = resp.data;
  //     setAllDbData(data);
  //   },
  //   [updateShift, addNewShift],
  //   [allocateShift]
  // );
  // const convertDate = (str) => {
  //   str = str.toString();
  //   let parts = str.split(" ");
  //   let months = {
  //     Jan: "01",
  //     Feb: "02",
  //     Mar: "03",
  //     Apr: "04",
  //     May: "05",
  //     Jun: "06",
  //     Jul: "07",
  //     Aug: "08",
  //     Sep: "09",
  //     Oct: "10",
  //     Nov: "11",
  //     Dec: "12",
  //   };
  //   return parts[2] + "/" + months[parts[1]] + "/" + parts[3];
  // };

  const add_shift = async () => {
    let obj = {};
    // let date = convertDate(startDate);
    // obj.Date = date;
    obj.StartingHour = startHour_new;
    obj.EndingHour = endingHour_new;

    await shiftsUtils.add_shift(token, obj);
    setAdded(!added);
  };

  return (
    <div>
      <Typography variant="h4" pb={2} color="primary.second">
        <b> Shifts Page</b>
      </Typography>
      <ButtonGroup fullWidth>
        <div>
          <Typography variant="h6" pb={2} color="primary.second">
            <b> Add new shift</b>
          </Typography>{" "}
          <div>
            <LocalizationProvider dateAdapter={DateAdapter}>
              Choose Date:
              <DesktopDatePicker
                label="Date desktop"
                inputFormat="MM/dd/yyyy"
                value={value}
                onChange={handleChange}
                renderInput={(params) => <TextField {...params} />}
              />
            </LocalizationProvider>
          </div>
          Starting Hour:
          <input
            type="text"
            placeholder="08:00"
            onChange={(e) => setStartHour_new(e.target.value)}
          />
          <br />
          Ending Hour:
          <input
            type="text"
            placeholder="18:00"
            onChange={(e) => setEndingHour_new(e.target.value)}
          />{" "}
          <br />
          <input type="button" value="Add shift" onClick={add_shift} />
          {added && <div>Added!</div>}
        </div>
        <Button
          fullWidth
          sx={{ m: 1 }}
          variant="contained"
          onClick={() => setAddNewShift(!addNewShift)}
        >
          Add new shift
        </Button>
        <Button
          fullWidth
          sx={{ m: 1 }}
          variant="contained"
          onClick={() => setUpdateShift(!updateShift)}
        >
          Update shift
        </Button>
        <Button
          fullWidth
          sx={{ m: 1 }}
          variant="contained"
          onClick={() => setAllocateShift(!allocateShift)}
        >
          Allocate employees to shift
        </Button>
      </ButtonGroup>
      {allocateShift && (
        <div>
          <h3>Allocate Shifts:</h3>
          {storeData.db.shifts.map((item, index) => {
            return (
              <ShiftAllocate
                key={index}
                shift={item}
                employees={storeData.db.employees}
              />
            );
          })}
        </div>
      )}
      {updateShift && (
        <div>
          <h3>Update Shifts:</h3>
          {storeData.db.shifts.map((item, index) => {
            return (
              <ShiftComp
                key={index}
                shift={item}
                employees={storeData.db.employees}
              />
            );
          })}
        </div>
      )}
    </div>
  );
};

export default ShiftsPage;
